primeslist = [p512,p1024,p2048,p4096,p8192,p16384,p32768,p65536]


def saveSSS():
    for p in primeslist :
        Fp = field.Field(p)
        SSSp = secretsharing.ShamirSecretSharing(Fp,3,5)
        SSSp.save('SSS'+str(len(bin(p))-2))
    

